Vue.config.devtools = true
new Vue({
  el: '#app',
  data: () => ({
    dialog: false,
    drawer: false,
    new_post: false,
    share_speed_dial: false,
    text_edit_mode: false,
    app_color: "teal darken-3",
    icon_color: "teal--text text--darken-3",
    app_name: "My App",
    selected_item: "Home",
    style:{
      button_color: "teal darken-3",
      button_color_outline: "teal--text text--darken-3"
    },
    items: [
      { icon: 'home', text: 'Home' },
      {
        icon: 'keyboard_arrow_up',
        'icon-alt': 'keyboard_arrow_down',
        text: 'Post',
        model: false,
        children: [
          { text: 'New' },
          { text: 'Edit' }
        ]
      },
      { icon: 'settings', text: 'Settings' },
      { icon: 'chat_bubble', text: 'Send feedback' },
      { icon: 'help', text: 'Help' },
      { icon: 'phonelink', text: 'App downloads' },
      { icon: 'keyboard', text: 'Go to the old version' }
    ],
    main_items: {
      current_select: true
    },
    gradient_list: {
      color_name_list: color_name_list,
      color_set_list: color_set_list,
      color_index: index,
      rotate_arr: ["to right,", "", "to left,", "to top,"],
      rotate_index: 0
    }
  }),
  created: function () {
    that = this
    $(document).ready(function(){
      $(".draggable").draggable()
      that.showActionList()
      setTimeout(function(){
        $("#list_action [md-fab-trigger]").click()
      }, 500)
    })

    $(document).on("click", "[md-fab-trigger]", function(e){
      e.currentTarget
        .closest('.md-speed-dial')
        .classList.toggle('md-active')
    })

    this.clickOutSide("#list_action", this.editMode)
  },
  watch: {
    items(newItems){
      console.log(newItems)
    },
  },
  computed: {
    getStyleBackground(){
      gradient_list = this.gradient_list
      color_set = gradient_list.color_set_list[gradient_list.color_index].split("!")
      string_color = color_set.toString()
      string_color = string_color.slice(0, string_color.length-1)
      return "linear-gradient(" + gradient_list.rotate_arr[gradient_list.rotate_index] + string_color + ")"
    },
  },
  methods: {
    clickOutSide(element, callback){
      app = $("[data-app]") || $("body")
      $(document).on("click", app, function(e){
        if(!$(e.target).closest(element).length){
          callback()
        }
      })
    },

    showActionList(){
      console.log("aaaa")
    },
    changeAppName(app_name){
      new_post = true
      this.selected_item = app_name
    },
    rotateGradien(){
      this.gradient_list.rotate_index += 1
      if(this.gradient_list.rotate_index >= this.gradient_list.rotate_arr.length){
        this.gradient_list.rotate_index = 0
      }
    },
    downloadImage(){
      image_name = this.gradient_list.color_name_list[this.gradient_list.color_index]
      clone_content = $("main").clone().empty()
      clone_content.insertBefore("main")
      html2canvas($("main")[0], {
        onrendered: function(canvas) {
          clone_content.remove()
          var a = document.createElement('a');
          a.href = canvas.toDataURL("image/jpeg").replace("image/jpeg", "image/octet-stream");
          a.download = image_name;
          a.click();
        }
      });
    },
    changeFavicon(){
      clone_content = $("main").clone().empty()
      clone_content.insertBefore("main")
      html2canvas(clone_content, {
        onrendered: function(canvas) {
          clone_content.remove()
          var img_data = canvas.toDataURL("image/jpeg")
          $("#icon").attr("href", canvas.toDataURL("image/jpeg"))
        }
      });
    },
    switchTextMode(){
      $("#list_action [md-fab-trigger]").click()
      that = this
      setTimeout(function(){
        that.text_edit_mode = !that.text_edit_mode
        setTimeout(function(){
          $("#list_action [md-fab-trigger]").click()
        }, 10)
      }, 600)
    },
    editMode(){
      if(that.text_edit_mode){
        this.switchTextMode()
      }
    }
  },
  props: {
    source: String
  }
})
